export const generateHash = (ids) => {
	const sortedStr = [...ids].sort().join('');
	const base64 = btoa(sortedStr);
	return base64.split('').reverse().join('');
};

export const decodeReasoning = (encoded) => {
	try {
		return atob(encoded);
	} catch (e) {
		return "";
	}
};
